mapper.select:
select s.id, s.name, s.no, s.gender, s.phone, s.degree, s.id_card as idCard, s.is_college as isCollege, s.address, s.graduation_date as graduationDate, s.violation_count as violationCount, s.violation_score as violationScore, s.clazz_id as clazzId, s.create_time as createTime, s.update_time as updateTime, c.name as clazzName from student s left join clazz c on c.id = s.clazz_id
and s.name like concat('%', #{name}, '%')
and s.degree = #{degree}
and s.clazz_id = #{clazzId}
order by s.id desc

mapper.update:
update student
name = #{name}, no = #{no}, gender = #{gender}, phone = #{phone}, degree = #{degree}, clazz_id = #{clazzId}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, graduation_date = #{graduationDate}, violation_count = #{violationCount}, violation_score = #{violationScore}, update_time = #{updateTime}
where id = #{id}
